import { useState } from 'react'
import blogservice from '../services/blogs'


const Blog = ({blog, addLike,username, handledelete}) =>{
  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  const [blogvisable, setblogvisable]=useState(false)

  const hideWhenVisible = { display: blogvisable ? 'none' : '' }
  const showWhenVisible = { display: blogvisable ? '' : 'none' }
  const handleDelete = async () => {
    if(window.confirm(`Remove blog ${blog.title}`)){
      const id = blog.id
      const token = JSON.parse(window.localStorage.loggedBlogappUser).token
      await blogService.removeBlog(id, token)
      props.updateBlogs()
    }
    props.setNotification(`${blog.title} was removed`)
    setTimeout(() => {
      props.setNotification(null)
    }, 5000)
  }

  return(
    <div>
      <div style={hideWhenVisible} style={blogStyle}>
          {blog.title} {blog.author} <button onClick={() => setblogvisable(true)}>view</button>
      </div>
      <div style={showWhenVisible}>
      {blog.title} <br />
      {blog.url} <br />
      {blog.likes} <button onClick={addLike}>like</button> 
      {username} <br />
      <button onClick={() =>setblogvisable(false)}>Hide</button> <br />
      <button onClick={handleDelete}>Remove</button>

      </div>
     
      
    </div>
  )
}

export default Blog
