import { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'

import BlogForm from './components/BlogForm'
import LoginForm from './components/LoginForm'
import Togglable from './components/Togglable'


const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('') 
  const [password, setPassword] = useState('') 
  const [errorMessage, setErrorMessage] = useState(null)
  const [user, setUser] = useState(null)
  
  const handleLogin = async (event) => {
    event.preventDefault()
    
    try {
      const user = await loginService.login({
        username, password,
      })

      window.localStorage.setItem(
        'loggedNoteappUser', JSON.stringify(user)
      ) 
      blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
    } catch (exception) {
      setErrorMessage('Wrong credentials')
      setTimeout(() => {
        setErrorMessage(null)
      }, 5000)
    }
  }
  
  const handlelogout=(event)=>{
    event.preventDefault()
    setErrorMessage(`${user.name} has loged out`)
    setTimeout(() => {setErrorMessage(null)}, 3500)
    setUser(null)
    window.localStorage.clear()
  
    }

  const addBlog = (blogObject) => {
    blogService
      .create(blogObject)
      .then(returnedBlog => {
        setBlogs(blogs.concat(returnedBlog))
        setErrorMessage('a new blog added: ' + blogObject.title)

        setTimeout(() => {
          setErrorMessage(null)
        }, 2000)
      })
  }

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )  
  }, [])
  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedNoteappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  const likeBlog =  async (blog) => {
    console.log('here is blogvalue', blog)
    const likedBlog = { ...blog, likes: blog.likes + 1 }
    await blogService.update(blog.id,likedBlog)
    const updateblogs = await blogService.getAll()
    setBlogs(updateblogs)
}
const deleteBlog = async (blog) => {
  if (window.confirm(`Delete ${blog.title}?`)){
     await blogService.deleteId(blog.id)
     
  }

}


  const Showblog = () => (
    <div>
      {blogs
        .sort((a, b) => b.likes - a.likes)
        .map(blog => (
          <Blog key={blog.id} blog={blog} addLike={()=>likeBlog(blog)} username={user.username} handledelete={deleteBlog} />
        ))}
    </div>
  )

return (
  <div>
    <h1>blogs</h1>
    {errorMessage}

    {user === null ?
        <Togglable buttonLabel='login'>
          <LoginForm
            username={username}
            password={password}
            handleUsernameChange={({ target }) => setUsername(target.value)}
            handlePasswordChange={({ target }) => setPassword(target.value)}
            handleSubmit={handleLogin}
          />
        </Togglable> :
        <div>
          <p>{user.name} logged-in <button onClick={handlelogout}>logout</button></p>
          
          <Togglable buttonLabel="new Blog">
            <BlogForm addBlog={addBlog}/>
          </Togglable>
          {Showblog()}
          
          
        </div>
      }
  </div>
)

}

export default App
