import { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'
import axios from 'axios'


const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('') 
  const [password, setPassword] = useState('') 
  const [errorMessage, setErrorMessage] = useState(null)
  const [user, setUser] = useState(null)
  const [title, settitle]=useState('')
  const [author, setauthor]= useState('')
  const [url, seturl]=useState('')

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedNoteappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])
  
   
  const handleLogin = async (event) => {
    event.preventDefault()
    
    try {
      const user =  await axios.post(`http://localhost:3003/api/login`, {username, password})
     // const user = await loginService.login({
       // username, password,
    //  })
    window.localStorage.setItem(
      'loggedNoteappUser', JSON.stringify(user)
    )
    blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
    } catch (exception) {
      setErrorMessage('Wrong credentials')
      setTimeout(() => {
        setErrorMessage(null)
      }, 5000)
    }
  }
  const createblog= async(event)=>{
    event.preventDefault()
    try{
      const blogObject = {
        title: title,
        author: author,
        url: url,
        id: id,
        
      }
      blogService.create(blogObject, user.token).then(blog => {
        setBlogs(blogs.concat(blog))
        author.reset()
        title.reset()
        url.reset()
        setErrorMessage(
          `a new blog ${blogObject.title} by ${blogObject.author} added`
        )
      })
      setTimeout(() => {
        setErrorMessage(null)
      }, 3000)
    }
    catch (exception) {
      setErrorMessage('blogin lis채채minen ei onnistunut')
      setTimeout(() => {
        setErrorMessage(null)
      }, 3000)
    }
   
  }
  
  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )  
  }, [])

  const loginForm = () => (
    <form onSubmit={handleLogin}>
      <div>
        username
          <input
          type="text"
          value={username}
          name="Username"
          onChange={({ target }) => setUsername(target.value)}
        />
      </div>
      <div>
        password
          <input
          type="password"
          value={password}
          name="Password"
          onChange={({ target }) => setPassword(target.value)}
        />
      </div>
      <button type="submit">login</button>
    </form>      
  )

  const blogform = ()=>(
    <form onSubmit={createblog}>
      <h2>Create new</h2>

      <div>title <input
        type="text"
        id="title"
        value={title}
        name="title"
        onChange={({ target }) => settitle(target.value)}
      /></div>

      <div>author <input
        type="text"
        id="author"
        value={author}
        name="author"
        onChange={({ target }) => setauthor(target.value)}
      /></div>

      <div>url <input
        type="text"
        id="url"
        value={url}
        name="url"
        onChange={({ target }) => seturl(target.value)}
      /></div>

      <button type="submit">create</button>

    </form>
  
  )

  if (user === null) {
    return(
      <div>
        <h2>log in to application</h2>
        {errorMessage}
        {loginForm()}
      </div>
    )
  }
  const handlelogout=(event)=>{
  event.preventDefault()
  setErrorMessage(`${user.data.name} has loged out`)
  setTimeout(() => {setErrorMessage(null)}, 3500)
  setUser(null)
  window.localStorage.clear()

  }

  return (
    <div>
      <h2>blogs</h2>
      <p>{user.data.name} logged-in <button onClick={handlelogout}>logout</button></p>
      {blogform()}
      {blogs.map(blog =>
        <Blog key={blog.id} blog={blog} />
      )}
    </div>
  )
}

export default App
