import blogservice from '../services/blogs'


const Blog = ({blog, add}) =>{
  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  const [blogvisable, setblogvisable]=useState(false)

  const hideWhenVisible = { display: blogvisable ? 'none' : '' }
  const showWhenVisible = { display: blogvisable ? '' : 'none' }

  const handleLike = () => {
    const { author, url, title, id } = blog
    addLike({
        likes: blog.likes + 1,
        user: blog.user.id,
        author,
        url,
        title,
        id
    })
}
 
  return(
    <div>
      <div style={hideWhenVisible} style={blogStyle}>
          {blog.title} {blog.author} <button onClick={() => setblogvisable(true)}>view</button>
      </div>
      <div style={showWhenVisible}>
      {blog.title} <br />
      {blog.url} <br />
      {blog.likes} <button>like</button> <br /> <br />
      <button onClick={() =>setblogvisable(false)}>Hide</button>

      </div>
     
      
    </div>
  )
}

export default Blog
